sys_script_fix.script:
var woGR = new GlideRecord('x_nuvo_eam_clinical_work_orders');
woGR.addEncodedQuery('closed_atISEMPTY^state=3^ORstate=7');
woGR.setLimit(1);
woGR.query();

while (woGR.next()){
	woGR.setValue('closed_At',woGR.sys_updated_on); // this date can be changed to a few options depending on customer want

}
