while(gr.next()){
		count++;
}
if(count <= 2)
{
	util.handleOutput('Only the admin and ITIL role are included in the Report View ACL. Apply the "HTM-CW-Thailand-MM-9090-CRG" update set to fix.','warning','HTM Asset Management');
