x_nuvo_diag_probes.script:
var util = new x_nuvo_diag.DiagnosticUtil();

var x = util.checkDuplicateOverrides('Calibration Management');

if (x != false) {
	util.handleOutput('There is a Nuvolo Property ' + x + ', that has two overrides within the Nuvolo Properties table. Each Nuvolo Property should have at maximum one override value', 'Warning','Calibration');
}

var y = util.checkDuplicateOverrides('Calibration Management Report');

if (y != false) {
	util.handleOutput('There is a Nuvolo Property ' + y + ', that has two overrides within the Nuvolo Properties table. Each Nuvolo Property should have at maximum one override value', 'Warning','Calibration');
}

var z = util.checkDuplicateOverrides('Calibration Management UI');

if (z != false) {
	util.handleOutput('There is a Nuvolo Property ' + z + ', that has two overrides within the Nuvolo Properties table. Each Nuvolo Property should have at maximum one override value', 'Warning','Calibration');
}
