x_nuvo_diag_probes.script:
var util = new x_nuvo_diag.DiagnosticUtil();

var gr = new GlideRecord('x_nuvo_eam_stops');
gr.addQuery('table','x_nuvo_eam_clinical_devices');
gr.addQuery('sys_class_name','x_nuvo_eam_stops');
gr.query();

if(gr.next())
	util.handleOutput('One or more stops in the x_nuvo_eam_stops table are not linked correctly to the clinical stops table. Please change the sys_class_name to coincide with the appropriate Clinical Devices table.','warning','HTM Asset Management');
